// Parse the OpenAI response to extract the summary details
const aiResponse = items[0].json.response.choices[0].message.content;

// Extract original email information
const originalEmail = {
  from: items[0].json.from,
  subject: items[0].json.subject,
  text: items[0].json.text,
  to: items[0].json.to,
  messageId: items[0].json.messageId
};

// Categorize the email based on AI summary
let category = 'general';
let requiresHumanAttention = false;
let autoReplyAppropriate = true;

// Check for appointment request
if (aiResponse.toLowerCase().includes('appointment request') || 
    aiResponse.toLowerCase().includes('book a session') || 
    aiResponse.toLowerCase().includes('schedule a massage')) {
  category = 'appointment';
}

// Check for cancellation
if (aiResponse.toLowerCase().includes('cancel') || 
    aiResponse.toLowerCase().includes('reschedule')) {
  category = 'cancellation';
}

// Check for urgent issues
if (aiResponse.toLowerCase().includes('urgent') || 
    aiResponse.toLowerCase().includes('emergency') || 
    aiResponse.toLowerCase().includes('immediate')) {
  requiresHumanAttention = true;
}

// Check for complex inquiries that might need human attention
if (aiResponse.toLowerCase().includes('refund') || 
    aiResponse.toLowerCase().includes('complaint') || 
    aiResponse.toLowerCase().includes('dissatisfied') ||
    aiResponse.toLowerCase().includes('injury') ||
    aiResponse.toLowerCase().includes('pain')) {
  requiresHumanAttention = true;
}

// Return the processed data
return {
  json: {
    ...items[0].json,
    summary: aiResponse,
    category,
    requiresHumanAttention,
    autoReplyAppropriate,
    originalEmail
  }
};

